$(document).ready(function(){$("#hamburger-menu").on("click",function(){$(".menu a").toggleClass("open")})});

$(document).ready(function(){$(".filter").on("click",function(){$(".categories").toggleClass("open")})});

//adding and removing filter items through checkbox

$(document).ready(function(){

  $('input:checkbox').change(function(){

    if (this.checked) {
    var value = $("input[type='checkbox']:checked").val();
    $(".filter-wrap ul").append('<li>' + 'x ' + this.value + '</li>');
  } else {
    $(".filter-wrap li").last().remove();
  }
    });
});
